      var marker = L.marker([mark.a, mark.o],{
        icon: L.divIcon({ className: 'map_icon s_1 i_'+mark.c+' id_'+mark.i }),
        riseOnHover: true,
      }).addTo(markerLayer);
      markerc[mark.i] = marker;
      $('.id_'+mark.i).css('cursor','grab');
